package com.kh.network;

import java.net.InetAddress;
import java.net.UnknownHostException;

public class Run {

	public static void main(String[] args) {
		try {
		InetAddress localhost = InetAddress.getLocalHost();
		System.out.println(localhost); //내 pc명 + 내 ip주소
		
		System.out.println("내 pc명 :"+ localhost.getHostNmae());
		System.out.println("내 IP주소 :"+ localhost.getHostAddress());
		
		System.out.println("========================================");
		
		InetAddress googleHost = InetAddress.getbyname("www.google.co.kr");
		
		System.out.println("구글 pc명 :"+ googleHost.getHostNmae());
		System.out.println("구글 IP주소 :"+ googleHost.getHostAddress());
		
		}catch(UnknownHostException e) {
			e.printStackTrace();                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
		}
		
		
	}

}
